#include <sys/param.h>
#include <sys/module.h>
#include <sys/kernel.h>
#include <sys/systm.h>

static int
hello_modevent(module_t mod __unused, int event, void *arg __unused)
{
    int error = 0;

    switch(event){
        case MOD_LOAD:
            uprintf("Hello, world!\n");
            break;
        case MOD_UNLOAD:
            uprintf("Good-bye, cruel world!\n");
            break;
        default:
            error = EOPNOTSUPP;
            break;
    }

    return (error);
}

static moduledata_t hello_mod = {
    "hello",
    hello_modevent,
    NULL
};

DECLARE_MODULE(hello, hello_mod, SI_SUB_DRIVERS, SI_ORDER_MIDDLE);


KMOD= hello
SRCS= hello.c

.include <bsd.kmod.mk>


這邊不得不說 FreeBSD 在開發 Kernel Module 的時候比 Linux 方便很多。
連 Makefile 的設都定幫你準備好。
在 include "bsd.kmod.mk" 之後，可以使用

make load
與

make unload
來代替 kldload ./hello.ko 與 kldunload hello.ko。
Note:

.. <sys/systm.h>:: 是 systm.h 不是 system.h



